{
  "linkedin": {
    "connection_request": {
      "character_limit": 300,
      "best_practices": [
        "Personalize with specific detail",
        "Mention mutual connection or interest",
        "Clear value proposition"
      ]
    },
    "message": {
      "character_limit": 1300,
      "best_practices": [
        "Hook in first sentence",
        "Break up text with paragraphs",
        "Include clear CTA"
      ]
    },
    "inmail": {
      "character_limit": 1900,
      "subject_limit": 200,
